T['cat'];    } else {        $category = $_POST['cat'];    }    if (strrpos($_POST['logo'], '/')) $_POST['logo'] = substr($_POST['logo'], strrpos($_POST['logo'], '/') + 1);    if (strrpos($_POST['logo_our'], '/')) $_POST['logo_our'] = substr($_POST['logo_our'], strrpos($_POST['logo_our'], '/') + 1);    if (strrpos($_POST['logo_top'], '/')) $_POST['logo_top'] = substr($_POST['logo_top'], strrpos($_POST['logo_top'], '/') + 1);    $_POST['textdesc'] = str_replace('="../img', '="./img', $_POST['textdesc']);    if ($_POST['status'] == "on") $status = 1;    else $status = 0;    $sql = "SELECT active FROM vender WHERE id='" . $_GET['edit'] . "'";    $result = mysql_query($sql, $db);    if ($row = mysql_fetch_row($result)) $oldstatus = $row[0];    $sql = "UPDATE vender SET name='" . mysql_real_escape_string($_POST['name']) . "',adress='" . mysql_real_escape_string($_POST['adress']) . "',phones='" . $_POST['phone'] . "',url='" . $_POST['URL'] . "',description='" . $_POST['textdesc'] . "',logo='" . $_POST['logo'] . "',category='" . $category . "',discount='" . $_POST['discount'] . "',active='" . $status . "',html_title='" . mysql_real_escape_string($_POST['title']) . "',html_keyword='" . mysql_real_escape_string($_POST['keyword']) . "',html_description='" . mysql_real_escape_string($_POST['description']) . "',mini_logo='" . $_POST['logo_our'] . "',logo_pos='" . $_POST['logo_our_pos'] . "',top_logo='" . $_POST['logo_top'] . "',top_logo_pos='" . $_POST['logo_top_pos'] . "',referal='" . $_POST['referal'] . "',id_admin='" . $_POST['manager'] . "', topshop='" . $_POST['topshop'] . "' WHERE id='" . $_GET['edit'] . "'";    $result = mysql_query($sql, $db);    if (!$result) echo("ошибка!!!!!");    if ($oldstatus != $status) {        if ($status == 0) {            $sql1 = "SELECT active_v FROM categories WHERE id='" . $_POST['cat'] . "'";            $result1 = mysql_query($sql1, $db);            if ($row = mysql_fetch_row($result1)) $active = $row[0] - 1;            if ($active >= 0) {                $sql = "UPDATE categories SET active_v='" . $active . "' WHERE id='" . $_POST['cat'] . "'";                $result = mysql_query($sql, $db);            }        } elseif ($status == 1) {            $sql1 = "SELECT active_v FROM categories WHERE id='" . $_POST['cat'] . "'";            $result1 = mysql_query($sql1, $db);            if ($row = mysql_fetch_row($result1)) echo $active = $row[0] + 1;            $sql = "UPDATE categories SET active_v='" . $active . "' WHERE id='" . $_POST['cat'] . "'";            $result = mysql_query($sql, $db);        }    }    echo '<script language="javascript">	    top.location.href="./' . $url_cat . '";	    </script>';    // -- Сохраняем картинки. Картинки переименовываются начиная с цифры последней картинки.    // -- Сохраняются под следующими номерами и с раширениями jpg, png, gif, jpeg.    // -- Сохраняются в папке с id вендера. Полный путь к картинке - ../img/partner/{{id}}/name_of_image.{{jpg,png,gif,jpeg}}. --    // -- Первое, нам нужно id вендера с кем работает --    $sql_query = "SELECT `id`, `name` FROM `vender` WHERE id='" . $_GET['edit'] . "'";    $mysql_query = mysql_query($sql_query, $db);    $arrayFetch = mysql_fetch_row($mysql_query);    // -- Правильный путь до папки с партнером --    $path = $_SERVER['DOCUMENT_ROOT'] . '/img/partner/' . $arrayFetch[0];    // -- Проверяем, есть ли папка --    if (is_dir($path)) {    } else {        mkdir($path);    }    // -- Если она есть, тогда проверяем сколько там файлов и какой номер у последнего файла --    // -- Сканирует папку на количество файлов.    // -- array_diff выводит разницу между двумя массивами.    // -- В данном случае он вернет именно тот массив имен, который нужен, без точек    // -- т.к. scandir сканирует папку с точками. --    $items = array_diff(scandir($path), array('..', '.'));    // -- Счетчик картинок --    $cnt = 0;    // -- Перечисляем все файлы. --    foreach ($items as $key => $item) {        // -- Первое, что нужно сделать, это соединить все пути, т.е. получается полный путь к картинке с именем и расширением --        $filePath = $path . '/' . $item;        // -- Дальше, чтобы убрать расширение мы используем функцию pathinfo --        $filename = pathinfo($filePath, PATHINFO_FILENAME);        // -- Теперь, нам нужно подсчитать количества файлов, которое будет равно последнему имени картинки.        // -- При этом, мы должны убрать все маленькие картинки, чтобы можно было сосчитать --        if (strpos($filename, 'sm_') === 0) continue;        // -- Счетчик, куда заносятся количества картинок без расширения и без маленький превьюшек --        $cnt++;    }    // -- Это нужно для того, чтобы маленькие картинки имели точно такое же имя, как и для старших --    // -- Нужно просто для того, если вообще заливаются большие картинки, так же само и с маленькими --    $numberOfImage = $cnt;    if ($_FILES['image']) {        foreach ($_FILES['image']['name'] as $k => $f) {            if (!$_FILES['image']['error'][$k]) {                if ((($_FILES["image"]["type"][$k] == "image/gif")                    || ($_FILES["image"]["type"][$k] == "image/jpeg")                    || ($_FILES["image"]["type"][$k] == "image/png")                    || ($_FILES["image"]["type"][$k] == "image/pjpeg"))                ) {                    if (is_uploaded_file($_FILES['image']['tmp_name'][$k])) {                        // -- Присваиваем имя файла равное имя последнего файла в папке + 1 --                        $nameFile = $_FILES['image']['name'][$k];                        preg_match('/(.+)?\.(.+)?$/', $nameFile, $output);                        // -- Получаем его MIME-тип --                        $mime = $output[2];                        // -- Изменяем его имя --                        $file = $cnt + 1;                        // -- Чтобы имя файла увеличилось, нужно увеличить и счетчик --                        $cnt++;      