бок нет то отправляем данные                return true;            }            else {                if (error == 1) var err_text = "Не все обязательные поля заполнены";                $("#messenger").html(err_text);                $("#messenger").fadeIn("slow");                return false; //если в форме встретились ошибки , не  позволяем отослать данные на сервер.            }        })    });</script><?php/** * Created by Godod. * Date: 18.02.13 * Time: 11:25 */if ($_POST['submit']) {    $to = 'freeb1986@ukr.net';    $from = 'info@freebonline.com';    $title = 'Заказ сертификата';    $sql = "SELECT `name`,`howballs`,`howcost` FROM `certificate` WHERE `id`='" . $_POST['certificate'] . "' LIMIT 1";    $mysql = mysql_query($sql, $db);    $rowAssoc = mysql_fetch_assoc($mysql);    $message = "$_POST[name] заказал(а) сертификат $rowAssoc[name]. Стоимость сертификата $rowAssoc[howcost],    а номинальная сумма $rowAssoc[howballs]. Номер телефона - $_POST[number_phone]";    mail($to, $title, $message, "From: $from");    ?><script>    alert('Наш менеджер скоро перезвонит Вам!');    setTimeout('location.replace("http://www.freebonline.com")', 500);    /*Изменить текущий адрес страницы через 0.5 секунды (500 миллисекунд)*/</script><noscript>    <meta http-equiv="refresh" content="1; url=http://www.freebonline.com"></noscript><?php} else {    ?><div class="vender_main">Как получить сертификат от free-B</div><div style="margin: 20px 0; color:#666" align="justify"><?php    $sql = "SELECT `description` FROM page WHERE id=" . $_GET['page'];    $query = mysql_query($sql, $db);    $row = mysql_fetch_row($query);    echo $row[0];    /**     * Таблица с отсылкой на почту письма с номером карточки, именем, телефоном, а также какой сертификат заказал     */    ?>    <br>    <br>    <div hidden="hidden" id="messenger"></div>    <form class="text_certificate" method="post"> 