<?phpif( $_POST[ 'enter' ] ){    $sql = "SELECT * FROM `member` WHERE login='" . $_POST[ 'login' ] . "' AND password='" . md5( $_POST[ 'upass' ] ) . "' LIMIT 1";    $res = mysql_query( $sql, $db ) or die( 'Не могу соединиться с базой' );    if( $row = mysql_fetch_assoc( $res ) ){        $_SESSION[ 'datareg' ] = $row['datereg'];        $_SESSION[ 'id_user' ] = $row['id'];        if( empty( $row['t_name'] ) && empty( $row[ 'f_name' ] ) && empty( $row['s_name'] ) )            $_SESSION[ 'user_name' ] = '(пусто)';        else            $_SESSION[ 'user_name' ] = $row['t_name'] . ' ' . $row['f_name'];        $_SESSION[ 't_name' ] = $row[ 't_name' ];        $_SESSION[ 'f_name' ] = $row[ 'f_name' ];        $_SESSION[ 's_name' ] = $row['s_name'];        if( empty( $row[ 'phone' ] ) )            $_SESSION[ 'phone' ] = '(пусто)';        else            $_SESSION[ 'phone' ] = $row[ 'phone' ];        $_SESSION[ 'birthday' ] = $row[ 'birthday' ];        if( empty( $row[ 8 ] ) )            $_SESSION[ 'email' ] = '(пусто)';        else            $_SESSION[ 'email' ] = $row[ 8 ];        $_SESSION[ 'balls' ]        = $row[ 'balls' ];        $_SESSION[ 'code' ]         = $row[ 'code' ];        $_SESSION[ 'change_balls' ] = $row[ 'change_balls' ];        $_SESSION[ 'saved_money' ]  = $row[ 'saved_money' ];        $_SESSION[ 'country' ]      = $row[ 'country' ];        $_SESSION[ 'city' ]         = $row[ 'city' ];        if( $row[ 'sex' ] )            $_SESSION[ 'sex' ] = 'Мужской';        else            $_SESSION[ 'sex' ] = 'Женский';        $_SESSION[ 'all_balls' ]   = $row[ 'all_balls' ];        $_SESSION[ 'all_product' ] = $row[ 'all_product' ];        $_SESSION[ 'balance' ]     = $row[ 'balance' ];        $_SESSION[ 'diller' ]      = $row[ 'diller'  ];        $_SESSION[ 'money' ]       = $row[ 'money'  ];        echo '<script language="javascript">	top.location.href="/page/' . NUMBER_PAGE_PCABINET . '"	</script>';    }    else{        $_SESSION[ 'changed' ] = 7;        echo '<script language="javascript">	top.location.href="/page/2"	</script>';    }}if( $_GET[ 'exit' ] == 1 ){    unset( $_SESSION[ 'id' ], $_SESSION[ 'user_name' ], $_SESSION[ 'phone' ], $_SESSION[ 'birthday' ], $_SESSION[ 'email' ], $_SESSION[ 'balls' ], $_SESSION[ 'change_balls' ], $_SESSION[ 'saved_money' ], $_SESSION[ 'country' ], $_SESSION[ 'city' ], $_SESSION[ 'code' ] );    unset( $_SESSION[ 'id_user' ] );    $_SESSION[ 'trash' ] = array();    echo '<script language="javascript">	top.location.href="/";	</script>';}?><style>    .fordump1 {        color: #fff;        text-decoration: underline;    }    .fordump1:hover {        text-decoration: none;    }</style><form method="post">    <div style="margin-top:6px; color: #fff; margin-left:15px; line-height:10px;">        <a href="/page/25" class="fordump1" style="color:#fff;">как зайти на сайт?</a></div>    <div style="float:left; width:186px;">        <div class="div_input1" align="left">            <input type="text" name="login" maxlength="10" value="Номер телефона"                   class="enter_input1" onfocus="this.value='';"                   onblur="if (this.value == '') this.value='Номер телефона';">        </div>        <div class="div_input2">            <input type="password" name="upass" value="" title="Номер Вашей карточки со всеми нулями"                   class="enter_input2">        </div>    </div>    <div style="float:right; width:46px;">        <input type="submit" value="&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;" name="enter" class="enter_login_btn">    </div>    <div style="clear:both"></div></form>