<?$result = mysql_query( "SELECT * FROM page WHERE menu='1' ORDER BY position ASC", $db );while( $row = mysql_fetch_assoc( $result ) ){    if( $row[ 'id' ] == 1 ){        ?><a href="/" class="list"><? echo $row[ 'header' ]; ?></a><?    }    else{		$link = !empty( $row[ 'alias' ] ) ? $row[ 'alias' ] : 'page/' . $row[ 'id' ];        ?><a href="/<?php echo $link; ?>" class="list"><? echo $row[ 'header' ]; ?></a><?    }}?><div class="share42init"></div>